import { Link } from 'react-router-dom'
import YoutubeEmbed from './YoutubeEmbed'
import {
  Aside,
  Artist,
  AlbumStyled,
  Wrapper,
  DescriptionStyled,
  CoverImage,
  MediaLinks
} from './SongDetails.style'

const SongDetails = ({ details }) => {
  return (
    <Aside>
      <Link to={`/artist/${details.primary_artist.id}`}>
        <Artist> {details.primary_artist.name} </Artist>
      </Link>
      <Wrapper>
        <CoverImage src={details.header_image_url} alt="Song cover" />
        <DescriptionStyled content={details.description_preview}/>
      </Wrapper>
      {details.youtube_url && <YoutubeEmbed url={details.youtube_url} />}
      <MediaLinks>
        {details.media.map(media => 
          <li key={media.provider}>
            <a href={media.url} rel="noopener noreferrer">
              {media.provider}
            </a>
          </li>
        )}
      </MediaLinks>
      {details.album && <AlbumStyled album={details.album} />}
    </Aside>
  )
}

export default SongDetails
