import { useState, useEffect } from 'react'
import styled from 'styled-components'
import GlobalStyles from './GlobalStyles'
import './App.css'

import { getSongData } from './services/lyrics'

import Loader from './components/Loader'
import SearchBar from './components/SearchBar'
import SongSection from './components/SongSection'

function App() {
  const [selectedSongId, setSelectedSongId] = useState(null)
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [selectedSongData, setSelectedSongData] = useState(null)

  // Have you ever had lyrics of a song stuck in your head that you cant recall the name of?

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(selectedSongId) {
        setLoadingStatus(true)
        const songData = await getSongData(selectedSongId)
        setSelectedSongData(songData)
        setLoadingStatus(false)
      }
    }

    fetchMyAPI()
  }, [selectedSongId])

  console.log(selectedSongData)

  return (
    <AppContainer>
      <GlobalStyles />
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', fontSize: '1em', padding: '1em'}}> Adamus </div>
      <SearchBar setSelectedSongId={setSelectedSongId} />
      {loadingStatus
        ? <Loader />
        : <SongSection details={selectedSongData} />
      }
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em 1em 1em;
  margin: 0;
`

export default App;
