import { useEffect, useState } from 'react'
import { search } from '../../services/lyrics'
import SearchResults from './SearchResults'

import { Container } from './index.style'

const SearchBar = ({ setSelectedSongId }) => {
  const [searchInputValue, setSearchInputValue] = useState('')
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [results, setResults] = useState({ songs: [], artists: [], albums: [] })

  const [expanded, setExpanded] = useState(false)

  useEffect(() => {
    const fetchData = async () => {
      if(searchInputValue.length > 0) {
        // setLoadingStatus(true)
        let response = await search(searchInputValue)

        setResults(response)
        // setLoadingStatus(false)
      } else {
        setResults({ songs: [], artists: [], albums: [] })
      }
    }

    // Timeout set to avoid overlapping requests
    const timeoutId = setTimeout(() => fetchData(), 250)
    return () => clearTimeout(timeoutId)
  }, [searchInputValue])

  const handleSongSelect = (id) => {
    setExpanded(false)
    setSelectedSongId(id)
  }

  return (
    <Container onFocus={() => setExpanded(true)} onBlur={() => setExpanded(false)}>
      <h2> Search </h2>
      <input
        placeholder='Search a song name or lyrics'
        value={searchInputValue}
        onChange={({target}) => setSearchInputValue(target.value)}
        spellCheck='false' />
        {loadingStatus
          ? <div> loading ... </div>
          : <SearchResults results={results} handleSongSelect={handleSongSelect} expanded={expanded} />
        }
    </Container>
  )
}

export default SearchBar
