import styled from 'styled-components'

export const AlbumContainer = styled.li`
  display: flex;
  background: #212121;
  box-shadow: 0px 0px 1px white;
`

export const AlbumCover = styled.img`
  width: auto;
  height: 10em;
  justify-self: center;
  align-self: start;
  padding: 1em;
`

export const AlbumContent = styled.div`
  width: 50ch;
  padding: 1em;
`

export const AlbumName = styled.h4`
  margin: 0;
  padding-bottom: 1em;
`

export const TracksList = styled.ul`
  display: grid;
  grid-template-columns: 1fr 1fr;
  /* gap: 0.5em; */
`

export const Track = styled.li`
  font-size: 0.8em;
  cursor: pointer;
  /* box-shadow: 0px 0px 1px white; */
  box-sizing: padding-border-box;
  
  & > a {
    color: #b3b3b3;
    padding: 0.5em;
    
    &:hover {
      color: white;
    }
  }
`
