import { useState, useEffect } from "react"
import styled from "styled-components"

const Description = ({ content }) => {
  const [textToShow, setTextToShow] = useState(content)
  const [extraContent, setExtraContent] = useState('')
  const [extraContentShow, setExtraContentShow] = useState(false)

  useEffect(() => {
    if (textToShow.length > 700) {
      setTextToShow(content.slice(0, 700))
      setExtraContent(content.slice(700, content.length))
    }
  // eslint-disable-next-line
  }, [textToShow])

  console.log(extraContent)

  return (
    <Container>
      {textToShow}
      {(extraContent.length > 0 && !extraContentShow) &&
        <button onClick={() => setExtraContentShow(true)}> Read More... </button>
      }
      {extraContentShow &&
        extraContent
      }
    </Container>
  )
}

const Container = styled.span`
  grid-row: 2;
  grid-column: 3;
  max-width: 70ch;
  /* box-shadow: 0px 0px 1px black; */
`

export default Description
