import { useState, useEffect } from 'react'
import './App.css'
import styled from 'styled-components'

import { search, getSongData } from './services/lyrics'

function App() {
  const [searchResultData, setSearchResultData] = useState({ songs: [], artists: [], albums: [] })
  const [searchStatusLoading, setSearchStatusLoading] = useState(false)
  const [searchInputValue, setSearchInputValue] = useState("")

  const [selectedSongId, setSelectedSongId] = useState(null)
  const [selectedSongData, setSelectedSongData] = useState(null)

  // Have you ever had lyrics of some song that you dont remember the name stuck in your head?

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(searchInputValue.length > 0) {
        setSearchStatusLoading(true)
        let response = await search(searchInputValue)

        setSearchResultData(response)
        setSearchStatusLoading(false)
      } else {
        setSearchResultData({ songs: [], artists: [], albums: [] })
      }
    }

    // Timeout set to avoid overlapping requests
    const timeoutId = setTimeout(() => fetchMyAPI(), 100)
    return () => clearTimeout(timeoutId)
  }, [searchInputValue])

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(selectedSongId) {
        const songData = await getSongData(selectedSongId)
        setSelectedSongData(songData)
      }
    }

    fetchMyAPI()
  }, [selectedSongId])

  return (
    <AppContainer>
      <h2> Search </h2>
      <SearchBar>
        <input
          placeholder="Search a song name or lyrics"
          value={searchInputValue}
          onChange={({target}) => setSearchInputValue(target.value)}
          spellCheck="false" />
        <ul>
          {searchStatusLoading
            ? <div> loading ... </div>
            : searchResultData.songs.length > 0
              ? searchResultData.songs.map(song => 
                  <li key={song.result.id} onClick={() => setSelectedSongId(song.result.id)}>
                    <img src={song.result.header_image_thumbnail_url} alt={`thumbnail`} />
                    <span> {song.result.full_title} </span>
                  </li>
                )
              : <div> No results :/ </div>
          }
        </ul>
        <button> Toggle </button>
      </SearchBar>
      <h2> Lyrics </h2>
      {selectedSongData
        && <Lyrics dangerouslySetInnerHTML={{ __html: selectedSongData.lyrics.replaceAll("[", "<span>[").replaceAll("]", "]</span>") }} />
      }

    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em;
  margin: 0;
`

const SearchBar = styled.div`
  box-shadow: 0px 0px 1px black;

  & > input {
    width: 100%;
    height: 1em;
    font-size: 1em;
    padding: 1em 0.2em;
    box-sizing: border-box;

    &:focus {
      outline: none;

      & ~ ul {
        /* display: flex; */
      }
    }
  }

  & > ul {
    display: flex;  /* default flex */
    flex-direction: column;
    max-height: 23em;
    overflow-y: scroll;

    & > li {
      display: grid;
      grid-template-columns: auto 1fr;
      align-items: center;
      column-gap: 1em;
      box-shadow: 0px 0px 1px black;
      padding: 0em;
      cursor: pointer;

      & > img {
        height: 5em;
        width: 5em;
        object-fit: cover;
      }

      &:hover {
        background: rgb(240, 240, 240);
      }
    }

    & > div {
      text-align: center;
    }
  }

  position: relative;
  & > button {
    position: absolute;
    right: 0;
    top:0;
    height: 3em;
  }
`

const Lyrics = styled.div`
  white-space: pre-wrap;
  width: fit-content;

  & span {
    font-weight: bold;
  }
`

export default App;
