  /* max-width: 50ch; */
  overflow: hidden;

  @media(max-width: 1000px) {
    min-width: 0;
    width: 100%;
  }
`

const TrackList = styled.ul`
  display: flex;
  flex-direction: column;
  margin-top: 1em;

  & > h3 {
    letter-spacing: 1px;
  }

  & > li > a {
    display: block;
    padding: 1em;
    /* box-shadow: 0px 0px 1px white; */
    color: #b3b3b3;

    &:hover {
      background: #212121;
      color: white;
    }
  }

  @media(max-width: 1000px) {
