import { useState, useEffect } from 'react'
import './App.css'
import styled from 'styled-components'

import { search } from './services/lyrics'

function App() {
  const [searchResultData, setSearchResultData] = useState({ songs: [], artists: [], albums: [] })
  const [searchInputValue, setSearchInputValue] = useState("")

  useEffect(() => {
    async function fetchMyAPI() {
      if(searchInputValue.length > 0) {
        let response = await search(searchInputValue)
        setSearchResultData(response)
      } else {
        setSearchResultData({ songs: [], artists: [], albums: [] })
      }
    }

    // Timeout set to avoid overlapping requests
    const timeoutId = setTimeout(() => fetchMyAPI(), 300)
    return () => clearTimeout(timeoutId)
  }, [searchInputValue])

  console.log(searchResultData)
  return (
    <AppContainer>
      <SearchBar>
        <input
        placeholder="Type a song, lyrics"
        value={searchInputValue} 
        onChange={({target}) => setSearchInputValue(target.value)} />
        <ul>
          {/* {result 
            ? result.map(song => 
                <li key={song.result.id}>
                  <img src={song.result.header_image_thumbnail_url} alt={`${song.result.full_title} thumbnail`} />
                  <span> {song.result.full_title} </span>
                </li>
              )
            : <div> Loading... </div>
          } */}
        </ul>
      </SearchBar>
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0;
  margin: 0;
`

const SearchBar = styled.div`
  & > input {
    width: 100%;
    height: 1em;
    font-size: 1em;
    padding: 1em 0.2em;
    box-sizing: border-box;
  }

  & > ul {
    display: grid;
    grid-template-columns: 1fr;

    & > li {
      display: grid;
      grid-template-columns: auto 1fr;
      align-items: center;
      column-gap: 1em;
      box-shadow: 0px 0px 1px black;
      padding: 1em;

      & > img {
        height: 3em;
        width: 3em;
      }
    }
  }
`

export default App;
