import ListItem from "./ListItem"

import { ListContainer, Thumbnail } from "./SearchResults.style"
import placeholderImg from './img/placeholder.png'

const ResultsList = ({ results, handleSongSelect, expanded }) => {
  return (
    <ListContainer expanded={expanded}>
      {/* {results.artists.length > 0
        && <>
          <h2> Artists </h2>
          {results.artists.slice(0, 2).map(artist => 
            <ListItem key={artist.result.id}>
              <Thumbnail 
              src={artist.result.header_image_url}
              onError={(e)=> { e.target.onerror = null; e.target.src = placeholderImg }} // Still returns 404 error if img url is invalid !!!
              alt={`thumbnail`} />
              <span> {artist.result.name} </span>
            </ListItem>
          )}
        </>
      } */}
      {results.songs.length > 0
        && 
          <>
            <h2> Songs </h2>
            {results.songs.map(song => 
              <ListItem
                id={song.result.id}
                thumbnail={song.result.header_image_thumbnail_url}
                title={song.result.full_title}
                handleSelect={handleSongSelect}
              />
            )}
          </>
      }
      {/* <h2> Albums </h2>
      {results.albums.length > 0
        && 
          <>
            {results.albums.slice(0, 2).map(album => 
              <ListItem key={album.result.id} onMouseDown={() => handleSongSelect(album.result.id)}>
                <Thumbnail
                src={album.result.cover_art_thumbnail_url}
                onError={(e)=> { e.target.onerror = null; e.target.src = placeholderImg }} // Still returns 404 error if img url is invalid !!!
                alt={`thumbnail`} />
                <span> {album.result.full_title} </span>
              </ListItem>
            )}
          </>
      } */}
    </ListContainer>
  )
}

export default ResultsList
