import { useState, useEffect } from 'react'
import styled from 'styled-components'
import GlobalStyles from './GlobalStyles'
import './App.css'

import { getSongData, getArtistData } from './services/lyrics'

import Loader from './components/Loader'
import SearchBar from './components/SearchBar'
import SongSection from './components/SongSection'

import appLogo from './components/Nav/img/appLogo.png'

function App() {
  const [loadingStatus, setLoadingStatus] = useState(false)
  // Songs
  const [selectedSongId, setSelectedSongId] = useState(null)
  const [selectedSongData, setSelectedSongData] = useState(null)
  // Artists
  const [selectedArtistId, setSelectedArtistId] = useState(null)
  const [selectedArtistData, setSelectedArtistData] = useState(null)

  // Have you ever had lyrics of a song stuck in your head that you cant recall the name of?

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(selectedSongId) {
        setLoadingStatus(true)
        const songData = await getSongData(selectedSongId)
        setSelectedSongData(songData)
        setLoadingStatus(false)
      }
    }

    fetchMyAPI()
  }, [selectedSongId])

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(selectedArtistId) {
        setLoadingStatus(true)
        const artistData = await getArtistData(selectedArtistId)
        setSelectedArtistData(artistData)
        setLoadingStatus(false)
      }
    }

    fetchMyAPI()
  }, [selectedArtistId])

  return (
    <AppContainer>
      <GlobalStyles />
      <div 
      onClick={() => {setSelectedSongData(null); setSelectedSongId(null)}} 
      style={{ width: '100%', height: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center', fontSize: '1em', padding: '1em 0', cursor: 'pointer' }}>
        <img src={appLogo} alt='scuffed spotify' style={{ height: '2em', width: 'auto' }}/> Spotifi
      </div>
      <SearchBar setSelectedSongId={setSelectedSongId} setSelectedArtistId={setSelectedArtistId} />
      {loadingStatus
        ? <Loader />
        : <SongSection details={selectedSongData} />
      }
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em 1em 1em;
  margin: 0;
`

export default App;
