import { useState, useEffect } from "react"
import styled from "styled-components"

const Description = ({ content }) => {
  const [textToShow, setTextToShow] = useState(content)
  const [extraContent, setExtraContent] = useState('')
  const [extraContentShow, setExtraContentShow] = useState(false)

  useEffect(() => {
    if (textToShow.length > 700) {
      setTextToShow(content.slice(0, 500))
      setExtraContent(content.slice(500, content.length))
    }
  // eslint-disable-next-line
  }, [textToShow])

  return (
    <Container>
      {textToShow}
      {(extraContent.length > 0 && !extraContentShow) &&
        <button onClick={() => setExtraContentShow(true)}> Read More... </button>
      }
      {extraContentShow &&
        extraContent
      }
    </Container>
  )
}

const Container = styled.span`
  grid-area: 1/2;
  max-width: 50ch;
  font-family: Rubik;

  & > button {
    border: 0;
    padding: 0;
    background: transparent;
    color: #b3b3b3;
    font-family: Rubik;
    letter-spacing: 1px;
    cursor: pointer;
    margin-left: 0.5em;
    
    &:hover {
      color: white;
    }
  }
  /* box-shadow: 0px 0px 1px black; */
`

export default Description
