import { useEffect, useState } from 'react'
import { useLocation } from 'react-router'
import styled from 'styled-components'
import GlobalStyles from './GlobalStyles'
import './App.css'

import { getSongData, getArtistData, getRandomSongs } from './services/lyrics'

import Loader from './components/Loader'
import SearchBar from './components/SearchBar'
import SwitchSection from './components/SwitchSection'

import appLogo from './components/Nav/img/appLogo.png'

function App() {
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [data, setData] = useState(null)
  const location = useLocation()

  // Have you ever had lyrics of a song stuck in your head that you cant recall the name of?
  const fetchData = async (id, type) => {
    switch(type) {
      case 'song':
        return await getSongData(id)
      case 'artist':
        return await getArtistData(id)
      default:
        return null
    }
  }

  const handleDataChange = async (id, type) => {
    window.location.pathname = `/${type}/${id}`
  }

  useEffect(() => {
    async function handleFetchData () {
      setLoadingStatus(true)
      try {
        setData(await fetchData(location.pathname.split('/')[2], location.pathname.split('/')[1]))
      } catch (error) {
        setData({ _type: 'error', content: '404' })
      }
      setLoadingStatus(false)
    }

    handleFetchData()
  }, [location])

  return (
    <AppContainer>
      <GlobalStyles />
      <AppLogo onClick={() => window.location.pathname = '/'}>
        <img src={appLogo} alt='scuffed spotify' /> Spotifi
      </AppLogo>
      <SearchBar setData={handleDataChange} />
      {loadingStatus
        ? <Loader />
        : data && <SwitchSection data={data} handleSelect={handleDataChange} />
      }
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em 1em 1em;
  margin: 0;
`

const AppLogo = styled.nav`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 1em 0;
  cursor: pointer;
  column-gap: 0.2em;

  & > img {
    height: 2em;
    width: auto;
  }
`

export default App;
