import { useState, useEffect } from 'react'
import './App.css'
import styled from 'styled-components'

import { getSongData } from './services/lyrics'
import SearchBar from './components/SearchBar'
import SongDetails from './components/SongDetails'

function App() {
  const [selectedSongId, setSelectedSongId] = useState(null)
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [selectedSongData, setSelectedSongData] = useState(null)

  // Have you ever had lyrics of a song stuck in your head that you cant recall the name of?
  console.log(selectedSongData)

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(selectedSongId) {
        setLoadingStatus(true)
        const songData = await getSongData(selectedSongId)
        setSelectedSongData(songData)
        setLoadingStatus(false)
      }
    }

    fetchMyAPI()
  }, [selectedSongId])

  return (
    <AppContainer>
      <SearchBar setSelectedSongId={setSelectedSongId} />
      {loadingStatus
        ? <span> LOADING </span>
        : <SongDetails details={selectedSongData} />
      }
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em;
  margin: 0;
`

export default App;
