import { useEffect, useState } from "react"
import styled from "styled-components"
import { search } from '../../services/lyrics'

const SearchBar = ({ handleSongSelect }) => {
  const [searchInputValue, setSearchInputValue] = useState("")
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [results, setResults] = useState({ songs: [], artists: [], albums: [] })
  
  const [expanded, setExpanded] = useState(false)

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(searchInputValue.length > 0) {
        setLoadingStatus(true)
        let response = await search(searchInputValue)

        setResults(response)
        setLoadingStatus(false)
      } else {
        setResults({ songs: [], artists: [], albums: [] })
      }
    }

    // Timeout set to avoid overlapping requests
    const timeoutId = setTimeout(() => fetchMyAPI(), 100)
    return () => clearTimeout(timeoutId)
  }, [searchInputValue])

  const toggleExpanded = () => {
    setExpanded(!expanded)
  }

  return (
    <Container expanded={expanded}>
      <input
          placeholder="Search a song name or lyrics"
          value={searchInputValue}
          onChange={({target}) => setSearchInputValue(target.value)}
          spellCheck="false" />
        <ul>
          {loadingStatus
            ? <div> loading ... </div>
            : results.songs.length > 0
              ? results.songs.map(song => 
                  <li key={song.result.id} onClick={() => handleSongSelect(song.result.id)}>
                    <img src={song.result.header_image_thumbnail_url} alt={`thumbnail`} />
                    <span> {song.result.full_title} </span>
                  </li>
                )
              : <div> No results :/ </div>
          }
        </ul>
        <button onClick={toggleExpanded}> Toggle list </button>
    </Container>
  )
}

const Container = styled.div`
  box-shadow: 0px 0px 1px black;
  
  & > input {
    width: 100%;
    height: 1em;
    font-size: 1em;
    padding: 1em 0.2em;
    box-sizing: border-box;

    &:focus {
      outline: none;
    }
  }

  & > ul {
    display: ${props => props.expanded ? 'flex' : 'none'};  /* default flex */
    flex-direction: column;
    max-height: 23em;
    overflow-y: scroll;

    & > li {
      display: grid;
      grid-template-columns: auto 1fr;
      align-items: center;
      column-gap: 1em;
      box-shadow: 0px 0px 1px black;
      padding: 0em;
      cursor: pointer;

      & > img {
        height: 5em;
        width: 5em;
        object-fit: cover;
      }

      &:hover {
        background: rgb(240, 240, 240);
      }
    }

    & > div {
      text-align: center;
    }
  }

  position: relative;
  & button {
    position: absolute;
    right: 0;
    top: 0;
    height: 2em;
  }
`

export default SearchBar
