import { Link } from 'react-router-dom'
import { Helmet } from 'react-helmet'
import styled from 'styled-components'
import Loader from '../Loader'
import Description from '../SectionSong/Description'

const SectionAlbum = ({ details }) => {
  return details && details._type === 'album' 
    ? <Container>
        <Helmet>
          <title> {details.name} </title>
        </Helmet>

        <Introduction>
          <CoverImage src={details.cover_art_url} alt='album cover' />
          <Name> {details.name} </Name>
          <DescriptionStyled content={details.description_preview} />
          <Link to={`/artist/${details.artist.id}`}>
            {details.artist.name}
          </Link>
          {/* <span> {details.release_date} </span>
          <span> {details.tracks.length} Tracks </span> */}
        </Introduction>
        {/* {details.artist.name} */}
        <TrackList>
          <h3> Tracks: </h3>
          {details.tracks.map(track =>
            <li key={track.song.id}>
              <Link to={`/song/${track.song.id}`}>
                {track.song.title}
              </Link>
            </li>
          )}
        </TrackList>
      </Container>
    : <Loader />
}

const Container = styled.section`
  display: flex;
  flex-direction: column;
  padding: 1em 0;
  column-gap: 1em;

  & a {
    color: #b3b3b3;

    &:hover {
      color: white;
    }
  }
`

const Introduction = styled.div`
  display: grid;
  grid-template-columns: auto 1fr;
  grid-template-rows: auto 1fr auto;
  column-gap: 1em;
  row-gap: 1em;

  @media(max-width: 1000px) {
    grid-template-columns: 1fr;
    justify-content: center;
    text-align: center;
  }
`

const CoverImage = styled.img`
  width: 15em;
  height: auto;
  box-shadow: 0px 0px 1px white;
  grid-row: 1/4;

  @media(max-width: 1000px) {
    justify-self: center;
    grid-row: 2;
  }
`

const Name = styled.h3`
  margin: 0;
  letter-spacing: 1px;

  @media(max-width: 1000px) {
    grid-row: 1;
  }
`

const DescriptionStyled = styled(Description)`
  /* max-width: 50ch; */
  overflow: hidden;

  @media(max-width: 1000px) {
    min-width: 0;
    width: 100%;
  }
`

const TrackList = styled.ul`
  display: flex;
  flex-direction: column;
  margin-top: 1em;

  & > h3 {
    letter-spacing: 1px;
  }

  & > li > a {
    display: block;
    padding: 1em;
    /* box-shadow: 0px 0px 1px white; */
    color: #b3b3b3;

    &:hover {
      background: #212121;
      color: white;
    }
  }

  @media(max-width: 1000px) {
    text-align: center;
  }
`

export default SectionAlbum
