import Loader from "../Loader"
import { RandomSongsList } from "./index.style"
import { Link } from "react-router-dom"

const SectionHome = ({ randomSongs, handleSelect }) => {
  return (
    <section>
      <h1> Random songs </h1>
      {randomSongs
        ? <RandomSongsList>
            {randomSongs.map(song =>
              <li key={song.id} onClick={() => handleSelect(song.id, 'song')}>
                <Link to={`/song/${song.id}`}> {song.full_title} </Link>
              </li>
            )}
          </RandomSongsList>
        : <Loader />
      }
    </section>
  )
}

export default SectionHome
