import { useState, useEffect } from 'react'
import './App.css'
import styled from 'styled-components'

import { search } from './services/lyrics'

function App() {
  const [searchResultData, setSearchResultData] = useState({ songs: [], artists: [], albums: [] })
  const [searchStatusLoading, setSearchStatusLoading] = useState(false)
  const [searchInputValue, setSearchInputValue] = useState("")

  useEffect(() => {
    async function fetchMyAPI() {
      if(searchInputValue.length > 0) {
        setSearchStatusLoading(true)
        let response = await search(searchInputValue)
        setSearchStatusLoading(false)
        setSearchResultData(response)
      } else {
        setSearchResultData({ songs: [], artists: [], albums: [] })
      }
    }
    // Timeout set to avoid overlapping requests
    const timeoutId = setTimeout(() => fetchMyAPI(), 300)
    return () => clearTimeout(timeoutId)
  }, [searchInputValue])

  console.log(searchStatusLoading)
  return (
    <AppContainer>
      <SearchBar>
        <input
        placeholder="Type a song, lyrics"
        value={searchInputValue} 
        onChange={({target}) => setSearchInputValue(target.value)} />
        <ul>
          {searchResultData
            ? searchResultData.songs.map(song => 
                <li key={song.result.id}>
                  <img src={song.result.header_image_thumbnail_url} alt={`thumbnail`} />
                  <span> {song.result.full_title} </span>
                </li>
              )
            : <div> Loading... </div>
          }
        </ul>
      </SearchBar>
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0;
  margin: 0;
`

const SearchBar = styled.div`
  & > input {
    width: 100%;
    height: 1em;
    font-size: 1em;
    padding: 1em 0.2em;
    box-sizing: border-box;

    &:focus {
      & ~ ul {
        display: flex;
      }
      outline: none;
    }
  }

  & > ul {
    display: none;  /* default flex */
    flex-direction: column;
    max-height: 23em;
    overflow-y: scroll;
    box-shadow: 0px 0px 2px black;

    & > li {
      display: grid;
      grid-template-columns: auto 1fr;
      align-items: center;
      column-gap: 1em;
      box-shadow: 0px 0px 1px black;
      padding: 1em;
      cursor: pointer;

      & > img {
        height: 3em;
        width: 3em;
      }
    }
  }
`

export default App;
