import styled from 'styled-components'
import { Link } from 'react-router-dom'
import Loader from '../Loader'

const SectionAlbum = ({ details, handleSelect }) => {
  return details && details._type === 'album' 
    ? <Container>
        <CoverImage src={details.cover_art_url} alt='album cover' />
        <div style={{ display: 'grid', gridTemplateColumns: '1fr', gridTemplateRows: 'auto 1fr auto' }}>
          <Name> {details.name} </Name>
          <Description>
            {details.description_preview}
          </Description>
          <span style={{ display: 'flex', columnGap: '1em' }}>
            <Link to={`/artist/${details.artist.id}`}>
              {details.artist.name}
            </Link>
            <span> {details.release_date} </span>
            <span> {details.tracks.length} Tracks </span>
          </span>
          {/* {details.artist.name} */}
        </div>
        <TrackList>
          {details.tracks.map(track =>
            <li key={track.song.id}>
              <Link to={`/song/${track.song.id}`}>
                {track.song.title}
              </Link>
            </li>
          )}
        </TrackList>
      </Container>
    : <Loader />
}

const Container = styled.section`
  display: flex;
  flex-direction: column;
  padding: 1em 0;
  column-gap: 1em;
`

const CoverImage = styled.img`
  width: 15em;
  height: auto;
  box-shadow: 0px 0px 1px white;
`

const Name = styled.h3`
  margin-top: 0;
`

const Description = styled.span`
  /* max-width: 50ch; */
`

const TrackList = styled.ul`
  grid-column: 1/3;
  display: flex;
  flex-direction: column;

  & > li > a {
    color: white;
    display: block;
    padding: 1em;

    &:hover {
      background: #212121;
    }
  }
`

export default SectionAlbum
