import { useEffect, useState } from 'react'
import { Switch, Route, Link } from 'react-router-dom'
import { useLocation, useHistory } from 'react-router'
import styled from 'styled-components'
import GlobalStyles from './GlobalStyles'
import './App.css'

import SectionArtist from './components/SectionArtist'
import SectionSong from './components/SectionSong'

import { getSongData, getArtistData } from './services/lyrics'

import SearchBar from './components/SearchBar'

import appLogo from './components/Nav/img/appLogo.png'

function App() {
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [data, setData] = useState(null)
  const location = useLocation()
  const history = useHistory()

  // Have you ever had lyrics of a song stuck in your head that you cant recall the name of?
  const fetchData = async (id, type) => {
    switch(type) {
      case 'song':
        return await getSongData(id)
      case 'artist':
        return await getArtistData(id)
      default:
        return null
    }
  }

  const handleDataChange = async (id, type) => {
    setData(null)
    history.push(`/${type}/${id}`)
  }

  useEffect(() => {
    const handleFetchData = async () => {
      setLoadingStatus(true)
      const [, id, path] = location.pathname.split('/')

      try {
        setData(await fetchData(path, id))
      } catch (error) {
        setData({ _type: 'error', content: '404' })
      }

      setLoadingStatus(false)
    }

    handleFetchData()
  }, [location])

  return (
    <AppContainer>
      <GlobalStyles />
      <AppLogo>
        <Link to='/'>
          <img src={appLogo} alt='scuffed spotify' /> Spotifi
        </Link>
      </AppLogo>
      <SearchBar setData={handleDataChange} />
      <Switch>
        <Route path='/artist/'>
          <SectionArtist details={data} handleSelect={handleDataChange} />
        </Route>
        <Route path='/song/'>
          <SectionSong details={data} />
        </Route>
        <Route path='/'>
          <div> Siema </div>
        </Route>
      </Switch>
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em 1em 1em;
  margin: 0;
`

const AppLogo = styled.nav`
  width: 100%;
  cursor: pointer;

  & > a {
    display: flex;
    justify-content: center;
    align-items: center;
    column-gap: 0.2em;
    color: white;
    padding: 1em 0;

    & > img {
      height: 2em;
      width: auto;
    }
  }
`

export default App;
