import axios from 'axios'
const baseUrl = 'https://api.genius.com'

let token = 'Bearer tQjb5FXY7yVaXkyn6_YmPjeky7Y70V9urJ7AAK3tzd3Z02nsab0vziEwVlk2sehD'

export const setToken = newToken => {
  token = `Bearer ${newToken}`
}

export const getLyricsOfSong = async name => {
  const config = {
    headers: { Authorization: token, query: name },
  }

  // https://api.genius.com/songs/378195?access_token=tQjb5FXY7yVaXkyn6_YmPjeky7Y70V9urJ7AAK3tzd3Z02nsab0vziEwVlk2sehD

  const id = '378195';
  const url = `https://api.genius.com/songs/${id}`
  const headers = {
      Authorization: token,
      "Access-Control-Allow-Origin": "*"
  };

  const response = await axios.get(url, { headers })

  console.log(response)
  return response.data
}
