import axios from 'axios'
const baseUrl = 'api.genius.com/'

let token = 'tQjb5FXY7yVaXkyn6_YmPjeky7Y70V9urJ7AAK3tzd3Z02nsab0vziEwVlk2sehD'

const setToken = newToken => {
  token = `bearer ${newToken}`
}

const getLyricsOfSong = async name => {
  const config = {
    headers: { Authorization: token },
  }

  const response = await axios.get(`${baseUrl}/search`, name, config)
  return response.data
}

export default { setToken, getLyricsOfSong }
