import axios from 'axios'
const baseUrl = 'https://api.genius.com'

let token = 'bearer tQjb5FXY7yVaXkyn6_YmPjeky7Y70V9urJ7AAK3tzd3Z02nsab0vziEwVlk2sehD'

export const setToken = newToken => {
  token = `bearer ${newToken}`
}

export const getLyricsOfSong = async name => {
  const config = {
    headers: { Authorization: token },
  }

  // const response = await axios.get(`${baseUrl}/search?q=Kendrick%20Lamar`, config)

  axios.get(`${baseUrl}/search?q=Kendrick%20Lamar`, {
    headers: {
      'Authorization': token
    }
  })
  .then((res) => {
    console.log(res.data)
  })
  .catch((error) => {
    console.error(error)
  })
  // console.log(response)
  // return response.data
}
