import { useState, useEffect } from 'react'
import './App.css'
import { search } from './services/lyrics'

function App() {
  const [result, setResult] = useState(null)
  const [searchInputValue, setSearchInputValue] = useState('')

  useEffect(() => {
    async function fetchMyAPI() {
      if(searchInputValue.length > 0) {
        let response = await search(searchInputValue)
        setResult(response)
      }
    }

    fetchMyAPI()
  }, [searchInputValue])

  console.log(result)

  return (
    <div>
      <input value={searchInputValue} onChange={({target}) => setSearchInputValue(target.value)} />
      <ul style={{ display: "grid", gridTemplateColumns: "auto 1fr"}}>
        {result 
          ? result.map(song => 
              <li key={song.result.id}>
                <img src={song.result.header_image_url} alt={`${song.result.full_title} thumbnail`} style={{ height: "3em", width: "auto" }} />
                {song.result.full_title}
              </li>
            )
          : <div> Loading... </div>
        }
      </ul>
    </div>
  );
}

export default App;
