import styled from "styled-components"

const SectionArtist = ({ details }) => {
  return (
    <Container>
      <h1> {details.name} </h1>
      <Description> {details.description_preview} </Description>
      <CoverImage src={details.image_url} alt={details.name} />
      <Albums>
        <h2> Albums </h2>
        {details.albums.map(album =>
          <li>
            <img src={album.cover_art_url} alt={album.name} />
            {album.name}
            {`${album.release_date_components.day}/${album.release_date_components.month}/${album.release_date_components.year}`}
          </li>
        )}
      </Albums>
    </Container>
  )
}

const Container = styled.section`
  display: grid;
  grid-template-columns: auto auto;
  grid-template-rows: auto auto;
`

const Description = styled.p`
  max-width: 60ch;
  grid-area: 2/1;
`

const Aside = styled.aside`
  grid-area: 1/2;
  grid-row: 1/3;
`

const CoverImage = styled.img`
  height: auto;
  width: 15em;
  grid-row: 1/3;
`

const Albums = styled.ul`
  grid-column: 1/3;
`

export default SectionArtist
