import styled from "styled-components"

export const Container = styled.div`
  position: relative;
  
  & > input {
    width: 100%;
    height: 1em;
    font-size: 1em;
    padding: 1.5em 0.2em;
    box-sizing: border-box;
    border: 0;
    box-shadow: 0px 0px 1px black;
    background: #212121;
    color: white;
    font-family: Rubik;

    &:focus {
      outline: none;
    }
  }
`
