import styled from 'styled-components'
import { Link } from 'react-router-dom'
import Loader from '../Loader'

const SectionAlbum = ({ details, handleSelect }) => {
  return details && details._type === 'album' 
    ? <div>
        <img src={details.cover_art_url} alt='album cover' />
        {details.name}
        {details.release_date}
        {details.description_preview}
        {details.artist.id}
        {details.artist.name}
        <TrackList>
          {details.tracks.map(track =>
            <li key={track.song.id}>
              <Link to={`/song/${track.song.id}`}>
                {track.song.title}
              </Link>
            </li>
          )}
        </TrackList>
      </div>
    : <Loader />
}

const TrackList = styled.ul`
  display: flex;
  flex-direction: column;

  & > li > a {
    color: white;
    padding: 1em 1em;
    box-sizing: padding-border-box;
  }
`

export default SectionAlbum
