import { useEffect, useState } from "react"
import styled from "styled-components"
import { search } from '../../services/lyrics'
import SearchResults from './SearchResults'

const SearchBar = ({ handleSongSelect }) => {
  const [searchInputValue, setSearchInputValue] = useState("")
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [results, setResults] = useState({ songs: [], artists: [], albums: [] })

  const [expanded, setExpanded] = useState(false)

  useEffect(() => {
    const fetchData = async () => {
      if(searchInputValue.length > 0) {
        setLoadingStatus(true)
        let response = await search(searchInputValue)

        setResults(response)
        setLoadingStatus(false)
      } else {
        setResults({ songs: [], artists: [], albums: [] })
      }
    }

    // Timeout set to avoid overlapping requests
    const timeoutId = setTimeout(() => fetchData(), 100)
    return () => clearTimeout(timeoutId)
  }, [searchInputValue])

  const toggleExpanded = () => {
    setExpanded(!expanded)
  }

  return (
    <Container>
      <input
          placeholder="Search a song name or lyrics"
          value={searchInputValue}
          onChange={({target}) => setSearchInputValue(target.value)}
          spellCheck="false" />
          {loadingStatus
            ? <div> loading ... </div>
            : results.songs.length > 0
              ? <SearchResults results={results.songs} handleSongSelect={handleSongSelect} expanded={expanded} />
              : <div> No results :/ </div>
          }
        <button onClick={toggleExpanded}> Toggle list </button>
    </Container>
  )
}

const Container = styled.div`
  box-shadow: 0px 0px 1px black;
  
  & > input {
    width: 100%;
    height: 1em;
    font-size: 1em;
    padding: 1em 0.2em;
    box-sizing: border-box;

    &:focus {
      outline: none;
    }
  }

  position: relative;
  & button {
    position: absolute;
    right: 0;
    top: 0;
    height: 2em;
  }
`

export default SearchBar
