import Loader from '../Loader'

const SectionAlbum = ({ details, handleSelect }) => {
  console.log(details)

  return details && details._type === 'album' 
    ? <div>
        {details.full_title}
        {details.cover_art_url}
        {details.release_date}
        {details.description_preview}
        {details.artist.id}
        {details.artist.name}
        <ul>
          {details.tracks.map(track =>
            <li key={track.song.id} onClick={() => handleSelect(track.song.id, 'song')}>
              {track.song.title}
            </li>
          )}
        </ul>
      </div>
    : <Loader />
}

export default SectionAlbum
