import styled from "styled-components"

export const LoaderContainer = styled.div`
  width: 100%;
  height: 100%;
  box-sizing: content-box;
  
  display: flex;
  justify-content: center;
  align-items: center;

  & img {
    width: 2em;
    height: 2em;
  }
`
