import styled from 'styled-components'
import YoutubeEmbed from './YoutubeEmbed'

const SongDetails = ({ details }) => {
  return details
    ? <Container>
        <Name> {details.full_title} </Name>
        <CoverImage src={details.header_image_url} alt="Song cover" />
        <Lyrics dangerouslySetInnerHTML={{ __html: details.lyrics.replaceAll("[", "<span>[").replaceAll("]", "]</span>") }} />
        <span> {details.description_preview} </span>
        <ul>
          {details.media.map(media => 
            <li key={media.provider}>
              <a href={media.url} rel="noopener noreferrer">
                {media.provider}
              </a>
            </li>
          )}
        </ul>
        <YoutubeEmbed url={details.youtube_url} />
      </Container>
    : <h2> Select a song </h2>
  
}

const Container = styled.section`
  display: grid;
  grid-template-columns: 1fr 1fr;
  justify-content: center;
  align-items: flex-start;
  width: 100%;
`

const Name = styled.h2`
`

const CoverImage = styled.img`
  height: 10em;
  width: auto;
  justify-self: center;
`

const Lyrics = styled.div`
  white-space: pre-wrap;
  width: fit-content;
  grid-row: 2/5;

  & span {
    font-weight: bold;
  }
`

export default SongDetails
