import { Link } from 'react-router-dom';
import { Container, Thumbnail } from './ListItem.style';
import placeholderImg from './img/placeholder.png'

const ListItem = ({ id, thumbnail, title, handleSelect, type }) => (
  <Container onMouseDown={(event) => event.preventDefault()} onClick={() => handleSelect(id, type)}>
    <Link to={`${type}/${id}`}>
      <Thumbnail
      src={thumbnail}
      onError={(e)=> { e.target.onerror = null; e.target.src = placeholderImg }} // Still returns 404 error if img url is invalid !!!
      alt={`thumbnail`} />
      <span> {title} </span>
    </Link>
  </Container>
)

export default ListItem
