import styled from "styled-components"
import Description from "../SectionSong/Description"

const SectionArtist = ({ details, handleSelect }) => {
  return (
    <Container>
      <CoverImage src={details.image_url} alt={details.name} />
      <div>
        <h1> {details.name} </h1>
        <DescriptionStyled content={details.description_preview} />
      </div>
      <Albums>
        <h2> Albums </h2>
        {details.albums.map(album =>
          <Album key={album.id}>
            <AlbumCover src={album.cover_art_url} alt={album.name} />
            <AlbumContent>
              <AlbumName> {album.name} </AlbumName>
              <TracksList>
                {album.tracks.slice(0, 10).map(track =>
                  <Track onClick={() => handleSelect(track.song.id, 'song')} key={track.song.id}> {track.song.title} </Track>
                )}
              </TracksList>
            </AlbumContent>
          </Album>
        )}
      </Albums>
    </Container>
  )
}

const Container = styled.section`
  display: flex;
  column-gap: 1em;
  padding-top: 1em;
  flex-wrap: wrap;

  & > div {
    max-width: 60ch;

    & > h1 {
      margin: 0;
      margin-bottom: 0.5em;
    }
  }
`

const DescriptionStyled = styled(Description)`
  align-self: flex-start;
`

const CoverImage = styled.img`
  height: 15em;
  width: auto;
  justify-self: end;
`

const Albums = styled.ul`
  display: flex;
  flex-wrap: wrap;
  gap: 1em;
  flex: 100%;
  justify-content: center;r

  & > h2 {
    width: 100%;
    margin: 0;
    margin-top: 1em;
  }
`

const Album = styled.li`
  display: flex;
  background: #212121;
  box-shadow: 0px 0px 1px white;
`

const AlbumCover = styled.img`
  width: auto;
  height: 10em;
  justify-self: center;
  align-self: center;
  padding: 1em;
`

const AlbumContent = styled.div`
  width: 50ch;
  padding: 1em;
`

const AlbumName = styled.h4`
  margin: 0;
  padding-bottom: 1em;
`

const TracksList = styled.ul`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.5em;
`

const Track = styled.li`
  font-size: 0.8em;
  color: #b3b3b3;
  cursor: pointer;

  &:hover {
    color: white;
  }
`

export default SectionArtist
