import styled from "styled-components"

const ResultsList = ({ results, handleSongSelect, expanded }) => {
  return (
    <ListContainer expanded={expanded}>
      {results.map(song => 
        <ListItem key={song.result.id} onClick={() => handleSongSelect(song.result.id)}>
          <Thumbnail src={song.result.header_image_thumbnail_url} alt={`thumbnail`} />
          <span> {song.result.full_title} </span>
        </ListItem>
      )}
    </ListContainer>
  )
}

const ListContainer = styled.ul`
  display: ${props => props.expanded ? 'flex' : 'none'};  /* default flex */
  flex-direction: column;
  max-height: 23em;
  overflow-y: scroll;
`

const ListItem = styled.li`
  display: grid;
  grid-template-columns: auto 1fr;
  align-items: center;
  column-gap: 1em;
  box-shadow: 0px 0px 1px black;
  padding: 0em;
  cursor: pointer;

  &:hover {
    background: rgb(240, 240, 240);
  }
`

const Thumbnail = styled.img`
  height: 5em;
  width: 5em;
  object-fit: cover;
`

export default ResultsList
