import { useState, useEffect } from 'react'
import styled from 'styled-components'
import GlobalStyles from './GlobalStyles'
import './App.css'

import { getSongData, getArtistData } from './services/lyrics'

import Loader from './components/Loader'
import SearchBar from './components/SearchBar'
import SongSection from './components/SongSection'

import appLogo from './components/Nav/img/appLogo.png'

function App() {
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [data, setData] = useState(null)

  // Have you ever had lyrics of a song stuck in your head that you cant recall the name of?

  const fetchData = async (id, type) => {
    switch(type) {
      case 'song':
        return await getSongData(id)
      case 'artist':
        return await getArtistData(id)
      default:
        return null
    }
  }

  const handleDataChange = async (id, type) => {
    setLoadingStatus(true)
    setData(await fetchData(id, type))
    setLoadingStatus(false)
  }

  console.log(data)

  return (
    <AppContainer>
      <GlobalStyles />
      <div
      style={{ width: '100%', height: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center', fontSize: '1em', padding: '1em 0', cursor: 'pointer' }}>
        <img src={appLogo} alt='scuffed spotify' style={{ height: '2em', width: 'auto' }}/> Spotifi
      </div>
      <SearchBar setData={handleDataChange} />
      {loadingStatus
        ? <Loader />
        : <SongSection details={data} />
      }
      {loadingStatus
        ? <Loader />
        : selectedArtistData &&
          <div>
            <p> {selectedArtistData.artist.name} </p>
            <p> {selectedArtistData.artist.description_preview} </p>
          </div>
      }
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em 1em 1em;
  margin: 0;
`

export default App;
