import SectionSong from '../SectionSong'
import SectionArtist from '../SectionArtist'
import error404 from '../../404.webp'

const SwitchSection = ({ data, handleSelect }) => {
  if (data._type === 'song')
    return <SectionSong details={data} />
  else if (data._type === 'album')
    return <h1> ALBUM </h1>
  else if (data._type === 'artist')
    return <SectionArtist details={data} handleSelect={handleSelect} />
  else if (data._type === 'error') {
    return <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', paddingTop: '2em' }}> 
      <img src={error404} alt='not found' style={{ height: 'auto', width: '15em' }} />
      {data.content} 
    </div>
  }
}

export default SwitchSection
