import styled from 'styled-components'
import Description from './Description'
import YoutubeEmbed from './YoutubeEmbed'

const SectionSong = ({ details }) => {
  return details
    ? <Container>
        <div>
          <Name> {details.full_title} </Name>
          <Lyrics dangerouslySetInnerHTML={{ __html: details.lyrics.replaceAll("[", "<span>[").replaceAll("]", "]</span>") }} />
        </div>

        <Details>
          <CoverImage src={details.primary_artist.header_image_url} alt="Artist cover" style={{ gridArea: '1/1'}} />
          <h2> {details.primary_artist.name} </h2>
          <CoverImage src={details.header_image_url} alt="Song cover" style={{ gridArea: '2/2' }}/>
          <Description content={details.description_preview}/>
          <YoutubeEmbed url={details.youtube_url} />
          <Links>
            {details.media.map(media => 
              <li key={media.provider}>
                <a href={media.url} rel="noopener noreferrer">
                  {media.provider}
                </a>
              </li>
            )}
          </Links>
        </Details>
      </Container>
    : <h2> Select a song </h2>
  
}

const Container = styled.section`
  display: grid;
  grid-template-columns: auto auto;
  grid-template-rows: auto 1fr;
  justify-content: space-between;
  width: 100%;
`

const Name = styled.h2`
  grid-column: 1/3;
  font-weight: 600;
  justify-self: start;
  margin-top: 1em;
  width: fit-content;
  padding-bottom: 0.3em;
  background: linear-gradient(0deg, white, white) bottom center no-repeat;
  background-size: 100% 2px;
`

const Lyrics = styled.div`
  white-space: pre-wrap;
  width: fit-content;

  & span {
    color: lightskyblue;
    font-size: 0.9em;
  }
`

const Details = styled.aside`
  display: grid;
  grid-template-columns: auto auto;
  width: fit-content;
  height: fit-content;
  row-gap: 1em;
  margin-top: 1.5em;
  column-gap: 1em;
`

const CoverImage = styled.img`
  height: 10em;
  width: auto;
  justify-self: start;
  box-shadow: 0px 0px 1px white;
`

const Links = styled.ul`
  display: flex;
  column-gap: 1em;
  width: 100%;
  justify-content: flex-end;
  grid-column: 1/3;

  & li a {
    color: #b3b3b3;
    
    &:hover {
      color: white;
    }
  }
`

export default SectionSong
