import styled from 'styled-components'

export const Main = styled.main`
  width: 100%;
  display: flex;
  flex-direction: column;

  & > h1 {
    margin: 0;
  }
`

export const OverviewParagraph = styled.p`
  font-size: 1.5em;
  max-width: 60ch;
  width: fit-content;
`
