import styled from 'styled-components'
import Description from './Description'

export const Container = styled.section`
  display: grid;
  grid-template-columns: 1fr auto;
  grid-template-rows: auto 1fr;
  justify-content: space-between;
  width: 100%;
`

export const Name = styled.h2`
  grid-column: 1/3;
  font-weight: 600;
  justify-self: start;
  margin-top: 1em;
  width: fit-content;
`

export const Lyrics = styled.div`
  white-space: pre-wrap;
  width: fit-content;

  & span {
    color: lightskyblue;
    font-size: 0.9em;
  }
`

export const Details = styled.aside`
  display: flex;
  flex-direction: column;
  width: fit-content;
  height: fit-content;
  row-gap: 1em;
  margin-top: 1.5em;
  column-gap: 1em;
`

export const Artist = styled.h5`
  font-weight: 600;
  letter-spacing: 1px;
  font-size: 2em;
  margin: 0;
  justify-self: start;
  cursor: pointer;
  color: white;
`

export const CoverImage = styled.img`
  flex: auto;
  height: 10em;
  width: auto;
  justify-self: start;
  box-shadow: 0px 0px 1px white;
`

export const DescriptionStyled = styled(Description)`
  width: 50ch;
`

export const MediaLinks = styled.ul`
  display: flex;
  column-gap: 1em;
  width: 100%;
  justify-content: flex-end;
  grid-column: 1/3;

  & li a {
    color: #b3b3b3;
    
    &:hover {
      color: white;
    }
  }
`
