import styled from "styled-components"
import Description from "../SectionSong/Description"
import Loader from "../Loader"
import Album from "./Album"

const SectionArtist = ({ details, handleSelect }) => {
  return details
    ? <Container>
        <CoverImage src={details.image_url} alt={details.name} />
        <div>
          <h1> {details.name} </h1>
          <DescriptionStyled content={details.description_preview} />
        </div>
        <Albums>
          <h2> Albums </h2>
          {details.albums.map(album =>
            <Album album={album} handleSelect={handleSelect} />
          )}
        </Albums>
      </Container>
    : <Loader />
}

const Container = styled.section`
  display: flex;
  column-gap: 1em;
  padding-top: 1em;
  flex-wrap: wrap;

  & > div {
    max-width: 60ch;

    & > h1 {
      margin: 0;
      margin-bottom: 0.5em;
    }
  }
`

const DescriptionStyled = styled(Description)`
  align-self: flex-start;
`

const CoverImage = styled.img`
  height: 15em;
  width: auto;
  justify-self: end;
`

const Albums = styled.ul`
  display: flex;
  flex-wrap: wrap;
  gap: 1em;
  flex: 100%;

  & > h2 {
    width: 100%;
    margin: 0;
    margin-top: 1em;
  }
`

export default SectionArtist
