import styled from 'styled-components'
import { Link } from 'react-router-dom'
import Loader from '../Loader'

const SectionAlbum = ({ details, handleSelect }) => {
  return details && details._type === 'album' 
    ? <Container>
        <CoverImage src={details.cover_art_url} alt='album cover' />
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          <Name> {details.name} </Name>
          <Description>
            {details.description_preview}
          </Description>
          <span>
            {/* {details.artist.id} */} {details.artist.name} {details.release_date} {details.tracks.length}

          </span>
          {/* {details.artist.name} */}
        </div>
        <TrackList>
          {details.tracks.map(track =>
            <li key={track.song.id}>
              <Link to={`/song/${track.song.id}`}>
                {track.song.title}
              </Link>
            </li>
          )}
        </TrackList>
      </Container>
    : <Loader />
}

const Container = styled.section`
  display: grid;
  grid-template-columns: auto 1fr;
  padding: 1em 0;
  column-gap: 1em;
`

const CoverImage = styled.img`
  height: 15em;
  width: 15em;
`

const Name = styled.h3`
  margin-top: 0;
`

const Description = styled.span`
  max-width: 50ch;
`

const TrackList = styled.ul`
  grid-column: 1/3;
  display: flex;
  flex-direction: column;

  & > li > a {
    color: white;
    display: block;
    padding: 1em;

    &:hover {
      background: #212121;
    }
  }
`

export default SectionAlbum
