import { useState, useEffect } from 'react'
import './App.css'
import styled from 'styled-components'

import { getSongData } from './services/lyrics'
import SearchBar from './components/SearchBar'

function App() {
  const [selectedSongId, setSelectedSongId] = useState(null)
  const [loadingStatus, setLoadingStatus] = useState(false)
  const [selectedSongData, setSelectedSongData] = useState(null)

  // Have you ever had lyrics of some song that you dont remember the name stuck in your head?

  useEffect(() => {
    const fetchMyAPI = async () => {
      if(selectedSongId) {
        setLoadingStatus(true)
        const songData = await getSongData(selectedSongId)
        setSelectedSongData(songData)
        setLoadingStatus(false)
      }
    }

    fetchMyAPI()
  }, [selectedSongId])

  return (
    <AppContainer>
      <h2 style={{ textAlign: "center" }}> Search </h2>
      <SearchBar setSelectedSongId={setSelectedSongId} />
      {loadingStatus 
        ? <span> LOADING </span>
        : selectedSongData
          ? <div>
              <h2> {selectedSongData.full_title} </h2>
              <Lyrics dangerouslySetInnerHTML={{ __html: selectedSongData.lyrics.replaceAll("[", "<span>[").replaceAll("]", "]</span>") }} />
            </div>
          : <h2> Select a song </h2>
      }
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em;
  margin: 0;

  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%fit-content;
`

const Lyrics = styled.div`
  white-space: pre-wrap;
  width: fit-content;

  & span {
    font-weight: bold;
  }
`

export default App;
