import styled from 'styled-components'

export const AlbumContainer = styled.li`
  display: flex;
  background: #212121;
  box-shadow: 0px 0px 1px white;
`

export const AlbumCover = styled.img`
  width: 10em;
  height: auto;
  justify-self: center;
  align-self: start;
  padding: 1em;
  
  filter: drop-shadow(0px 0px 1px rgba(255, 255, 255, 0.1));
`

export const AlbumContent = styled.div`
  width: 50ch;
  padding: 1em;
`

export const AlbumName = styled.h4`
  margin: 0;
  padding-bottom: 1em;
  letter-spacing: 1px;
`

export const TracksList = styled.ul`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.5em;
`

export const Track = styled.li`
  font-size: 0.8em;
  cursor: pointer;
  
  & > a {
    color: #b3b3b3;
  }

  &:hover > a {
    color: white;
  }
`
