import axios from 'axios'
const baseUrl = 'http://localhost:3001'

export const search = async name => {
  const response = await axios.get(`${baseUrl}/api/search/${name}`)
  return response.data
}

export const getSongData = async id => {
  const response = await axios.get(`${baseUrl}/api/song/${id}`)
  if (response.ok) {
    return response.data
  } else {
    return Promise.reject('error 404')
  }
}

export const getArtistData = async id => {
  const response = await axios.get(`${baseUrl}/api/artist/${id}`)
  if (response.ok) {
    return response.data
  } else {
    return Promise.reject('error 404')
  }
}
