import styled from "styled-components"
import Description from "../SectionSong/Description"

const SectionArtist = ({ details, handleSelect }) => {
  return (
    <Container>
      <h1> {details.name} </h1>
      <Description content={details.description_preview} />
      <CoverImage src={details.image_url} alt={details.name} />
      <Albums>
        <h2> Albums </h2>
        {details.albums.map(album =>
          <li>
            <img src={album.cover_art_url} alt={album.name} />
            <span>
              <h4> {album.name} </h4>
              <ul>
                {album.tracks.map(track => <li onClick={() => handleSelect(track.song.id, 'song')}> {track.song.title} </li>)}
              </ul> 
            </span>
            {/* {`${album.release_date_components.day}/${album.release_date_components.month}/${album.release_date_components.year}`} */}
          </li>
        )}
      </Albums>
    </Container>
  )
}

const Container = styled.section`
  display: grid;
  grid-template-columns: auto 1fr;
  grid-template-rows: auto auto;
  column-gap: 1em;
  padding-top: 1em;

  & > h1 {
    margin: 0;
  }
`

// const Description = styled.p`
//   max-width: 60ch;
//   grid-area: 2/1;
// `

const CoverImage = styled.img`
  height: 15em;
  width: auto;
  grid-row: 1/3;
  justify-self: end;
`

const Albums = styled.ul`
  grid-column: 1/3;
  display: flex;
  flex-wrap: wrap;
  gap: 1em;

  & > h2 {
    width: 100%;
  }

  & > li {
    display: flex;
    background: #212121;
    box-shadow: 0px 0px 1px white;

    & > img {
      width: auto;
      height: 10em;
    }
    
    & > span {
      width: 50ch;
      word-wrap: break-word;
      padding: 1em;
      
      & > h4 {
        margin: 0;
        padding-bottom: 1em;
      }

      & > ul {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 0.5em;

        & > li {
          font-size: 0.8em;
          color: #b3b3b3;
          cursor: pointer;

          &:hover {
            color: white;
          }
        }
      }
    }
  }
`

export default SectionArtist
