import styled from 'styled-components'
import { Link } from 'react-router-dom'
import Loader from '../Loader'

const SectionAlbum = ({ details, handleSelect }) => {
  return details && details._type === 'album' 
    ? <div>
        <div>
          <CoverImage src={details.cover_art_url} alt='album cover' />
          {details.name}
          {details.release_date}
          {details.description_preview}
          {details.artist.id}
          {details.artist.name}
        </div>
        <TrackList>
          {details.tracks.map(track =>
            <li key={track.song.id}>
              <Link to={`/song/${track.song.id}`}>
                {track.song.title}
              </Link>
            </li>
          )}
        </TrackList>
      </div>
    : <Loader />
}

const CoverImage = styled.img`
  height: 15em;
  width: auto;
`

const TrackList = styled.ul`
  display: flex;
  flex-direction: column;

  & > li > a {
    color: white;
    display: block;
    padding: 1em;

    &:hover {
      background: #212121;
    }
  }
`

export default SectionAlbum
