import styled from 'styled-components'

export const Container = styled.span`
  max-width: 50ch;
  font-family: Rubik;
  min-width: 30em;
`

export const ReadMoreButton = styled.button`
  border: 0;
  padding: 0;
  background: transparent;
  color: #add8e6;
  font-family: Roboto;
  letter-spacing: 1px;
  cursor: pointer;
  margin-left: 0.5em;
  font-size: 0.9em;
  
  &:hover {
    color: #72bcd4;
  }
`
