import axios from 'axios'
const baseUrl = 'https://api.genius.com'

let token = 'tQjb5FXY7yVaXkyn6_YmPjeky7Y70V9urJ7AAK3tzd3Z02nsab0vziEwVlk2sehD'

export const setToken = newToken => {
  token = `Bearer ${newToken}`
}

export const getLyricsOfSong = async name => {
  const id = '378195';
  const url = `https://api.genius.com/songs/${id}?access_token=${token}`

  const response = await axios.get(url)
  return response.data
}
