import { useEffect, useState } from 'react'
import { Switch, Route } from 'react-router-dom'
import { useLocation, useHistory } from 'react-router'
import styled from 'styled-components'
import GlobalStyles from './GlobalStyles'
import './App.css'

import { getSongData, getArtistData, getRandomSongs } from './services/lyrics'

import Nav from './components/Nav'
import SectionHome from './components/SectionHome'
import SearchBar from './components/SearchBar'
import SectionSong from './components/SectionSong'
import SectionArtist from './components/SectionArtist'
import NotFound from './components/NotFound'

const App = () => {
  const [data, setData] = useState(null)
  const [randomSongs, setRandomSongs] = useState(null)
  const location = useLocation()
  const history = useHistory()

  // Have you ever had lyrics of a song stuck in your head that you cant recall the name of?
  const fetchData = async (id, type) => {
    switch(type) {
      case 'song':
        return await getSongData(id)
      case 'artist':
        return await getArtistData(id)
      default:
        return null
    }
  }

  const handleDataChange = async (id, type) => {
    setData(null)
    history.push(`/${type}/${id}`)
  }

  useEffect(() => {
    const handleFetchData = async () => {
      try {
        setRandomSongs(await getRandomSongs(2))
      } catch (error) {}
    }

    handleFetchData()
  }, [])

  useEffect(() => {
    const handleFetchData = async () => {
      const [, id, path] = location.pathname.split('/')

      try {
        setData(await fetchData(path, id))
      } catch (error) {
        setData({ _type: 'error', content: '404' })
      }
    }

    handleFetchData()
  }, [location])


  return (
    <AppContainer>
      <GlobalStyles />
      <Nav />
      <SearchBar setData={handleDataChange} />
      <Switch>
        <Route path='/artist/'>
          {data && data._type === 'error' 
            ? <NotFound />
            : <SectionArtist details={data} handleSelect={handleDataChange} />
          }
        </Route>
        <Route path='/song/'>
          {data && data._type === 'error' 
            ? <NotFound />
            : <SectionSong details={data} />
          }
        </Route>
        <Route path='/'>
          <SectionHome randomSongs={randomSongs} handleSongSelect={handleDataChange} />
        </Route>
      </Switch>
    </AppContainer>
  )
}

const AppContainer = styled.div`
  padding: 0 1em 1em 1em;
  margin: 0;
  display: grid;
  grid-template-rows: auto auto 1fr;
  min-height: 100vh;
`

export default App;
