import { useState, useEffect } from 'react'
import './App.css'
import styled from 'styled-components'

import { search } from './services/lyrics'

function App() {
  const [result, setResult] = useState(null)
  const [searchInputValue, setSearchInputValue] = useState('')

  useEffect(() => {
    async function fetchMyAPI() {
      if(searchInputValue.length > 0) {
        let response = await search(searchInputValue)
        setResult(response)
      }
    }

    fetchMyAPI()
  }, [searchInputValue])

  return (
    <div>
      <SearchBar>
        <input value={searchInputValue} onChange={({target}) => setSearchInputValue(target.value)} />
        <ul>
          {result 
            ? result.map(song => 
                <li key={song.result.id}>
                  <img src={song.result.header_image_thumbnail_url} alt={`${song.result.full_title} thumbnail`} style={{ height: "3em", width: "3em" }} />
                  <span> {song.result.full_title} </span>
                </li>
              )
            : <div> Loading... </div>
          }
        </ul>
      </SearchBar>
    </div>
  )
}

const SearchBar = styled.div`
  & > ul {
    display: grid;
    grid-template-columns: 1fr;

    & > li {
      display: grid;
      grid-template-columns: auto 1fr;
      align-items: center;
      column-gap: 1em;
      box-shadow: 0px 0px 1px black;
    }
  }
`

export default App;
